import React from 'react';
import CollectionItem from './CollectionItem';

const CollectionList = ({ collections, onDelete, onAddCollection, onUpdateCollection }) => {
  const [newCollection, setNewCollection] = React.useState({ title: '', artist: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewCollection(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newCollection.title && newCollection.artist) {
      onAddCollection(newCollection);
      setNewCollection({ title: '', artist: '' });
    }
  };

  return (
    <div className="collection-list">
      <h2>Music Playlist</h2>
      <div className="collections">
        {collections.map((collection, index) => (
          <CollectionItem
            key={index}
            collection={collection}
            onDelete={onDelete}
            onUpdate={onUpdateCollection}
          />
        ))}
      </div>
    </div>
  );
};

export default CollectionList;
